INSERT INTO `qbmog_rsmonials_param` (`id`, `param_name`, `param_description`, `param_value`, `ordering`) VALUES
(2, 'admin_name', 'Name of Administrator', 'Site Administrator', 1),
(3, 'admin_email', 'Email Address of Administrator - This will use for sending and receiving Email', 'admin@yoursite.com', 2),
(4, 'show_desc', 'Set "false" to hide the description text appeared just agter the page title in front end. Set "true" to display', 'true', 3),
(5, 'login_to_submit_testimonial', 'Here you can specify who can post testimonials/comments. If you wish anyone can post testimonials then set the value of this field to "false". But if you wish only registered/logged in users can post comment then set the value to "true" here.', 'false', 4),
(6, 'show_single_name_field', 'Set "false" to display two name fields (i.e. "First Name" and "Last Name"). Set "true" to display single name field (i.e. "Your Name")', 'true', 5),
(7, 'show_about', 'Set "false" to hide "About You" field in front end. Set "true" to display', 'true', 6),
(8, 'show_location', 'Set "false" to hide "Your Location" field in front end. Set "true" to display', 'true', 7),
(9, 'show_website', 'Set "false" to hide "Your Website" field in front end. Set "true" to display', 'true', 8),
(10, 'show_image', 'Set "true" to enable image/picture uploading. Set "false" to disable.', 'true', 9),
(11, 'image_max_width', 'If you enabled "show_image", then please set the maximum allowed width of image (in pixel).', '500', 10),
(12, 'image_max_height', 'If you enabled "show_image", then please set the maximum allowed height of image (in pixel).', '500', 11),
(13, 'image_max_size', 'If you enabled "show_image", then please set the maximum allowed size of image to optimize server load(in kb).', '250', 12),
(14, 'show_captcha', 'Set "false" to hide "Captcha" field in front end. Set "true" to display', 'true', 13),
(15, 'use_recaptcha', 'Set "true" if you wish to use "ReCaptcha Library". To enable "ReCaptcha" you need to obtain ReCaptcha "Public API Key" and "Private API Key" from "www.google.com/recaptcha"\r\n\r\nSet "false" to use the component default captcha image.\r\n\r\nWe recommend you to enable ReCaptcha.', 'false', 14),
(16, 'recaptcha_public_key', 'If you are using "ReCaptcha" then please enter "ReCaptcha Public API Key" here.', 'xxx', 15),
(17, 'recaptcha_private_key', 'If you are using "ReCaptcha" then please enter "ReCaptcha Private API Key" here.', 'xxx', 16),
(18, 'recaptcha_theme', 'Four themes are currently available for ReCaptcha (Red, BlackGlass, White, Clean).\r\n\r\nEnter "1" to enable "Red Theme". Enter "2" to enable "BlackGlass Theme". Enter "3" to enable "White Theme". Enter "4" to enable "Clean Theme".', '4', 17),
(19, 'auto_approval', 'Set "false" if you wish to review all new testimonial first and then approve manually. Set "true" if you wish new testimonials will approve and start to dispaly instantly after submission', 'false', 18),
(20, 'show_pagination', 'Set "true" to show pagination in front end. Set "false" to show all testimonials in a single page', 'true', 19),
(21, 'pagination', 'Set how many testimonials you wish to display in a page. This will only work if you set (show_pagination = true)', '10', 20),
(22, 'pagination_alignment', 'Set the text alignment of the pagination => left, right or center', 'center', 21),
(23, 'admin_email_alert', 'Set "true" if you wish to receive one autogenerated email for each and every new testimonial posting. Set "false" to not receive autogenerated email', 'false', 22),
(24, 'blocked_ips', 'Block IP Addresses to post comment. You can add multiple IP addresses separated by comma.\r\n\r\nExample: 192.168.1.1, 202.101.50.x\r\n\r\nHow it works:\r\n1) 192.168.1.1 will block only this particular ip.\r\n2) 192.168.1.x will block all ip addresses starting with 192.168.1\r\n3) 192.168.x.x will block all ip addresses starting with 192.168\r\n4) 192.x.x.x will block all ip addresses starting with 192\r\n5) x.x.x.x will block all ip addresses.\r\n6) Set "false" to allow all ip addresses.', 'false', 23),
(25, 'show_website_as_link', 'Set "true" to display website as link (hyperlinked), set "false" to display website as plain text.', 'true', 8);

