INSERT INTO `qbmog_rsmonials_param_style` (`id`, `param_name`, `param_description`, `param_value`, `ordering`) VALUES
(1, 'testimonial_block_border', 'Border Style of each Testimonial block:\r\n\r\nExample 1: 1px solid #cccccc\r\nExample 2: 2px dotted #0000ff\r\nExample 3: 1px dashed #006600', '1px solid #dedede', 1),
(2, 'testimonial_block_background_color', 'Background Color of Testimonial Block:\r\n\r\nExample: #CCCCCC', '#FFFFFF', 2),
(3, 'testimonial_block_rounded_corner', 'Set "true" to get rounded corner of each testimonial block. Set "false" to get square block.\r\n\r\nNote: Rounded corner will not work in IE (upto IE 8). IE not supports it. From IE 9 it will work. IE9 is till to launch.', 'false', 3),
(4, 'testimonial_block_rounded_corner_radius', 'If you enabled "Rounded Corner", then you can set the Radius of the rounded corner (in pixel). By default it is 10. ', '10', 4),
(5, 'testimonial_block_enable_gradient', 'If you wish to use Gradient set this to "true", otherwise "False".\r\n\r\nNote: Gradient will not work in IE (upto IE 8). IE not supports it. From IE 9 it will work. IE9 is till to launch.', 'false', 5),
(6, 'testimonial_block_gradient_start_color', 'If you enabled "Gradient" then please set the start color of the gradient here.\r\n\r\nExample: #F7F7F7', '#F7F7F7', 6),
(7, 'testimonial_block_gradient_end_color', 'If you enabled "Gradient" then please set the start color of the gradient here.\r\n\r\nExample: #FFFFFF', '#FFFFFF', 7),
(8, 'testimonial_block_quotation_image_style', 'Enter "0" to "disable quotation image".\r\nEnter "1" to use "square type quotation image".\r\nEnter "2" to use "round type quotation image".', '2', 8),
(9, 'testimonial_block_default_image', 'If you enabled the image upload option in your testimonial form, then here you can set the default image.\r\n\r\nEnter "0" to "not display any image if someone do not upload his/her image".\r\nEnter "1" to use "Gray User image if there is no image".\r\nEnter "2" to use "Black User (Male) image if there is no image".\r\nEnter "3" to use "Black User (Female) image if there is no image".', '1', 9),
(10, 'testimonial_block_image_position', 'If you enabled the image upload option in your testimonial form, then here you can set the default image position.\r\n\r\nEnter "1" to "Display image in left side of testimonial".\r\nEnter "2" to "Display image in right side of testimonial".\r\nEnter "3" to "Display image in alternate side of testimonial (one in left, next one in right and so on...)".', '1', 10),
(11, 'testimonial_block_image_display_width', 'Display width of the testimonial image (in pixel).', '125', 11),
(12, 'testimonial_block_show_date', 'Enter "true" to display the date of submission of the testimonial.\r\nEnter "false" to not display the date of submission of the testimonial.', 'true', 12);

